using System; 
using System.Collections.Generic; 

public class Program
{
	public static List<int> Coins = new List<int>(); 
	public static void Main()
	{        
        int tot = 0 ;
        string strCoins = Console.ReadLine();
        var listCoins = strCoins.Split();
        foreach (var item in listCoins)
        {
            if (item == "penny") tot += 1;
            if (item == "nickel") tot += 5;
            if (item == "dime") tot += 10;
            if (item == "quarter") tot += 25;
        }

        decimal tot2 = Convert.ToDecimal(tot);
        tot2 /= 100;
        string tot3 = String.Format("{0:C}", tot2); 
        tot3 = tot3.Replace("Â¤","$");
        Console.WriteLine(tot3);	
	}
}
