using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{

	public static void Main()
	{
        string infoAboutHeight = Console.ReadLine();
        var SplitRay = infoAboutHeight.Split();
        double height = double.Parse(SplitRay[0]);

        int howMany = int.Parse(Console.ReadLine());

        double totalCost = 0;
        for(int i = 0 ; i < howMany ; i++)
        {
            string aLine = Console.ReadLine();
            var lineRay = aLine.Split();
            var DimensionsRay = (lineRay[2]).Split('x');
            int Dimension1 = int.Parse(DimensionsRay[0]);
            int Dimension2 = int.Parse(DimensionsRay[1]);
            double costPer = Convert.ToDouble(lineRay[3].Substring(1));
            string floorVSwall = lineRay[1];
            double thisCost = Dimension1 * Dimension2 * costPer;
            if (floorVSwall == "walls") thisCost = (Dimension1 + Dimension2) * 2 * height * .9 * costPer;

            totalCost += thisCost;
        }
        Console.WriteLine(MDF(totalCost));
	}

	public static string MDF(double parameter) // Make Dollar Format
	{
		string dollarAmt = String.Format("{0:C}", Convert.ToDecimal(parameter)).Replace("¤","$");	
		return dollarAmt;
	}
}
