using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{
	public static void Main()
	{
		var replacementRay = "0O 1I 3E 4A 5S 6G 7T 8B 0O".Replace(" ","").ToCharArray();

        int howMany = int.Parse(Console.ReadLine());

        for (int line = 0 ; line < howMany ; line++)
        {
            string cc = Console.ReadLine();
            for (int i = 0 ; i < replacementRay.Length ; i = i+2)
            {
                char ch1 = replacementRay[i];
                char ch2 = replacementRay[i+1];
                cc = cc.Replace(ch1,ch2);
            }
            Console.WriteLine(cc);
        }
    }
}
