using System; 
using System.IO; 
using System.Text; 
using System.Collections.Generic; 

public class Program
{
	public static char dot = ' ';
	public static char[,] Grid;
	public static int Hi;
	public static int Wi;
	public static int currentLin;
	public static int currentPos;
	public static int counter = 0;
	public static string material = "";

	public static string currentDirection;
	public static string u = "up";
	public static string d = "down";
	public static string l = "left";
	public static string r = "right";
	public static string rotationType;
	public static string cw = "clockwise";
	public static string ccw = "counter-clockwise";

	public static void Main()
	{
        counter = 0;
        material = "";
        var dirRay = "right down left up".Split();
        string input = Console.ReadLine();

        var inputRay = input.Split();
        int inputSize = int.Parse(inputRay[0]);

        string startingCorner = inputRay[1];
        rotationType = inputRay[2];

        currentLin = 0; 
        currentPos = 0;
        if (startingCorner.Contains("bottom")) currentLin = inputSize - 1; 
        if (startingCorner.Contains("Right")) currentPos = inputSize - 1;
        if (startingCorner == "topLeft") currentDirection = r;
        if (startingCorner == "topRight") currentDirection = d;
        if (startingCorner == "bottomRight") currentDirection = l;
        if (startingCorner == "bottomLeft") currentDirection = u;

        if (rotationType == ccw)
        {
            int dirRayIndex = Array.IndexOf(dirRay, currentDirection);
            dirRayIndex++;
            if (dirRayIndex == 4) dirRayIndex = 0;
            currentDirection = dirRay[dirRayIndex];

            Array.Reverse(dirRay); // <<< THEN (this has to come afterwards)
        }

        string datapoint1 = inputRay[3];
        string datapoint2 = inputRay[4];

        char charDP1 = datapoint1[0];
        int intDP1 = int.Parse(datapoint1.Substring(1));
        char charDP2 = datapoint2[0];
        int intDP2 = int.Parse(datapoint2.Substring(1));

        int diff1 = (int)charDP2 - (int)charDP1;
        int diff2 = intDP2 - intDP1; 

        material = "";

        char ch = charDP1;
        int inn = intDP1;

        while ((int) ch >= 'A' && ch <= 'Z' && inn > 0 )
        {
            material += new String(ch,inn);
            //prep for next round thru this loop:
            inn = inn + diff2;
            int i = ((int)ch) + diff1;
            ch = (char)i;
        } 

        Hi = Wi = inputSize;
        Grid = new char[Hi,Wi];

        InitGrid();

        Grid[currentLin, currentPos] = material[counter++];

        int printHowManyEstimate = (inputSize * inputSize);// probably significantly less than this
        for (int times = 0 ; times < printHowManyEstimate ; times++)
        {
            bool boo = CanIMoveInCurrentDirection();
            if (boo) 
            {
                MoveInCurrentDirection();
            }
            else
            {	
                int i = Array.IndexOf(dirRay,currentDirection);
                i++;
                if (i > 3) i = 0;
                currentDirection = dirRay[i];
            }
        }
        PrintGridOnly31();
	}

	public static void PrintGridOnly31()
	{
		if (Hi > 31) Hi = 31;
		if (Wi > 31) Wi = 31;
		for (int lin = 0 ; lin < Hi ; lin++)
		{
			for (int pos = 0 ; pos < Wi ; pos++)
			{
				char ch = Grid[lin,pos];
				Console.Write(ch);
			}
			Console.WriteLine();
		}
	}

	public static bool CanIMoveInCurrentDirection()
	{
		bool nextSpotIsAvailable = false;
		int nextLin = currentLin; // until proven otherwise in following
		int nextPos = currentPos; // until proven otherwise in following
		if (currentDirection == d) nextLin++;
		if (currentDirection == u) nextLin--;
		if (currentDirection == l) nextPos--; 
		if (currentDirection == r) nextPos++;
		if (nextLin < 0 || nextLin >= Hi || nextPos < 0 || nextPos >= Wi) 
		{
			//nextSpotIsAvailable = false;
		}
		else
		{
			char nextCh = Grid[nextLin,nextPos];
			if (nextCh == dot)
			{
				nextSpotIsAvailable = true;
			}
			else
			{
				//nextSpotIsAvailable = false;
			}
		}

		if (nextSpotIsAvailable) // now that it's passed the first test, now make sure that it has enough empty space around it
		{
			int skippedToNextLin = nextLin;
			int skippedToNextPos = nextPos;
			if (currentDirection == d) skippedToNextLin++;
			if (currentDirection == u) skippedToNextLin--;
			if (currentDirection == l) skippedToNextPos--; 
			if (currentDirection == r) skippedToNextPos++;

			if (skippedToNextLin < 0 || skippedToNextLin >= Hi || skippedToNextPos < 0 || skippedToNextPos >= Wi) // if you skipped to this, you'd be off the edge
			{
				return true;
			}
			else
			{
				char skippedToNextCh = Grid[skippedToNextLin,skippedToNextPos];
				if (skippedToNextCh == dot)                           // if its empty
				{
					return true;
				}
			}
		}
		return false;
	}

	public static void MoveInCurrentDirection()
	{
		int nextLin = currentLin; // until proven otherwise in following
		int nextPos = currentPos; // until proven otherwise in following
		if (currentDirection == d) nextLin++;
		if (currentDirection == u) nextLin--;
		if (currentDirection == l) nextPos--; 
		if (currentDirection == r) nextPos++;
		currentLin = nextLin;
		currentPos = nextPos;

		if (counter < material.Length)
		Grid[currentLin,currentPos] = material[counter++];
	}

	public static void InitGrid()
	{
		for (int lin = 0 ; lin < Hi ; lin++)
		{
			for (int pos = 0 ; pos < Wi ; pos++)
			{
				char ch = dot;
				Grid[lin,pos] = ch;
			}
		}
	}
}
