using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{
	public static void Main()
	{
		string input = Console.ReadLine();
		char ch1 = input[0];
		char ch2 = input[1];
		int howMany = int.Parse(input.Substring(2));
		int diff = (int)ch2 - (int)ch1;
		int counter = 0;
		do 
		{
			int i = (int)ch1 + counter++ * diff;
			char thisChar = (char)i;
			string s = new String(thisChar,counter);
			Console.WriteLine(s);
		} while (counter < howMany);	
	}
}
