using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        string text = Console.ReadLine();

        string output = text.ToUpper().Replace("A", "").Replace("E", "").Replace("I", "").Replace("O", "").Replace("U", "").Replace("Y", "");

        Console.WriteLine(string.IsNullOrWhiteSpace(output) ? "null" : output);
    }
}
