using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{
	public static string bl = " ";   // blank
	public static char dot = ' ';
	public static char star = '*';
	public static char plus = '+';
	public static char pipe = ':';
	public static char dash = '-';
	public static char[,] Grid; 
	public static int Hi, Wi;
	public static int TotalStars;
	public static Point PointA, PointB, PointC, PointD;
	public static List<Point> CROSS_POINTS = new List<Point>();
	public static double[,] Probabilities = new double[3,3] ; 

	public static void Main()
	{
		Hi = int.Parse(Console.ReadLine());

        List<string> inputs = new List<string>();
        for (int i = 0 ; i < Hi ; i++)
        {
            inputs.Add(Console.ReadLine());
        }

        Wi = inputs[0].Length;

		Grid = new char[Hi, Wi];

		for (int lin = 0 ; lin < Hi ; lin++)
		{
            string aLin = inputs[lin];
			for (int pos = 0 ; pos < Wi ; pos++)
			{
                Grid[lin,pos] = aLin[pos];
			}
		}

		PointA = new Point(0,0);
		PointD = new Point(Hi,Wi);

		POPULATE_CROSS_POINTS();
		PointB = CROSS_POINTS[0];
		PointC = CROSS_POINTS[3];

		var QuadRay = new Quadrant[3,3];

		var PointRay = new Point[] {PointA, PointB, PointC, PointD};

		for (int qLin = 0 ; qLin < 3 ; qLin++)
		{
			int ll = PointRay[qLin].Lin;
			int LL = PointRay[qLin+1].Lin;
			for (int qPos = 0 ; qPos < 3 ; qPos++)
			{
				int pp = PointRay[qPos].Pos;
				int PP = PointRay[qPos+1].Pos;
				Point PointTL = new Point(ll,pp); // TopLeft
				Point PointBR = new Point(LL,PP); // BottomRight
				Quadrant tempQuad = new Quadrant(PointTL, PointBR);

				QuadRay[qLin,qPos] = tempQuad;
			}
		}

		int TOTAL_STARS = CountStars();

		Console.WriteLine(TOTAL_STARS + " attendees");
		for (int i = 0 ; i < 3 ; i++)
		{
			string aLineAnswer = "";
			for (int j = 0 ; j < 3 ; j++)
			{
				QuadRay[i,j].COUNT_STARS_IN_THIS_QUAD();
				double perc = (100.0 * QuadRay[i,j].NUM_STARS) / TOTAL_STARS;
				aLineAnswer += "" + AddUnderLinesEtc(perc) + bl;
			}
			Console.WriteLine(aLineAnswer.Trim());
		}
	}

	public class Point
	{
		public int Lin;
		public int Pos;
		public Point(int lin, int pos)
		{
			Lin = lin;
			Pos = pos;
		}
	}

	public class Quadrant // Nonant
	{
		public Point TopLeft;
		public Point BottomRight;
		public int Capacity;
		public int NUM_STARS;

		public Quadrant (Point p1, Point p2)
		{
			TopLeft = p1;
			if (TopLeft.Lin != 0) TopLeft.Lin = TopLeft.Lin + 1;
			if (TopLeft.Pos != 0) TopLeft.Pos = TopLeft.Pos + 1; // for both these, it's go "right" or "down" from the borderlines
			BottomRight = p2;
		}

		public void COUNT_STARS_IN_THIS_QUAD()
		{
			int count = 0;
			for (int lin = TopLeft.Lin ; lin < BottomRight.Lin ; lin++)
			{
				for (int pos = TopLeft.Pos ; pos < BottomRight.Pos ; pos++)
				{
					int ch = Grid[lin,pos];
					if (ch == star) count++;
				}
			}
			NUM_STARS = count;
		}
	}

	public static string AddUnderLinesEtc(double perc)
	{
		perc = Math.Round(perc,0);
		string ret = "";
		if (perc < 100) ret += "_";
		if (perc < 10) ret += "_";
		return ret + perc + "%";	
	}

	public static void POPULATE_CROSS_POINTS()
	{
		for (int lin = 0 ; lin < Hi ; lin++)
		{
			for (int pos = 0 ; pos < Wi ; pos++)
			{
				char ch = Grid[lin,pos];
				if (ch == plus) CROSS_POINTS.Add(new Point(lin,pos));
			}
		}
	}

	public static int CountStars()
	{
		int count = 0;
		for (int lin = 0 ; lin < Hi ; lin++)
		{
			for (int pos = 0 ; pos < Wi ; pos++)
			{
				char ch = Grid[lin,pos] ;
				if (ch == star) count++;
			}
		}
		return count;
	}
}
