using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic; using System.Text.RegularExpressions;

public class Program
{
	public static char bl = ' ';   // blank

	public static void Main()
	{
        List<string> list= new List<string>();

        int mountainHeight = int.Parse(Console.ReadLine());
        for (int h = 0 ; h < mountainHeight ; h++)
        {
            string s =Console.ReadLine();
            list.Add(s);
        }

        foreach (var item in list)
        {
            Console.WriteLine(item);
        }

        string water = list.Last();
        water = ReplaceChar(water,'~');
        Console.WriteLine(water);

        list.Reverse();
        foreach (var item in list)
        {
            string itemLower= "";
            foreach (char ch in item)
            {
                char newChar = ' ';
                if (ch != bl)
                {
                    int i = (int)ch;
                    i = i + 32;
                    newChar = (char)i;
                }
                itemLower += newChar;
            }
            Console.WriteLine(itemLower);	
        }
	}

	public static string ReplaceChar(string strIn, char replacementCh)
	{
		string strOut= "";
		foreach (char ch in strIn)
		{
			char newChar = ' ';
			if (ch != bl)
			{
				newChar = replacementCh;
			}
			strOut += newChar;
		}
		return strOut;
	}
}
