import math

guitar_fondamental_frequencies=[329.6, 246.9, 196, 146.8, 110, 82.4]
ukulele_fondamental_frequencies=[440, 329.6, 261.6, 392]

#return the frequency related to a given pair string/fret for the guitar
def guitar_frequency(string, fret):
    return(guitar_fondamental_frequencies[string]*2**(fret/12))

#idem for the ukulele
def ukulele_frequency(string, fret):
    return(ukulele_fondamental_frequencies[string]*2**(fret/12))

#return the fret to press to obtain a given freqeuncy on a given string for the guitar
#if no match, return "no match"
def get_fret_guitar(string, frequency):
    fret=round(math.log(frequency/guitar_fondamental_frequencies[string],2)*12)
    if 0<=fret<=21:return(fret)
    else:return("no match")

#idem for the ukulele
def get_fret_ukulele(string, frequency):
    fret=round(math.log(frequency/ukulele_fondamental_frequencies[string],2)*12)
    if 0<=fret<=15:return(fret)
    else:return("no match")

mode=input()
n=int(input())
for _ in range(n):

    if mode=="guitar":
        #calculate the target frequency
        target_frequency=guitar_frequency(*map(int, input().split()))
        #check for each string if this frequency can be played on it
        match_list=[]
        for string in range(4):
            fret=get_fret_ukulele(string, target_frequency)
            if fret!="no match":
                match_list.append(str(string)+"/"+str(fret))
        #output
        if match_list:print(*match_list)
        else:print("no match")

    #same for ukulele mode
    if mode=="ukulele":
        target_frequency=ukulele_frequency(*map(int, input().split()))
        match_list=[]
        for string in range(6):
            fret=get_fret_guitar(string, target_frequency)
            if fret!="no match":
                match_list.append(str(string)+"/"+str(fret))
        if match_list:print(*match_list)
        else:print("no match")
