def fake_accounts(usernames):
    """(list of str) -> str

    Returns string containing all fakes.

    >>> fake_accounts('ANONYMOUS', 'i_i_i', 'i:')
    ANONYMOUS i_i_i i:
    """
    fakes = []
    for username in usernames:
        if 'anonymous' in username.lower():
            fakes.append(username)
        elif username.count('_') > 1:
            fakes.append(username)
        else:
            for char in username:
                if username not in fakes:
                    if not(char.isalpha()) and not(char.isnumeric()) and char != '_':
                        fakes.append(username)

    if len(fakes) == 0:
        return 'Cleared'

    ans = ''
    for fake in fakes:
        if fake != fakes[-1]:
            ans = ans + fake + ' '
        else:
            ans += fake

    return ans



usernames = []
num = int(input())
for i in range(num):
    usernames.append(input())

print(fake_accounts(usernames))
