def good_password(password):
    """(str) -> str

    Returns if password is good or not.

    >>> good_password('555555aA')
    BAD 1
    """
    ans = 'PERFECT'
    upper = False
    lower = False
    digit = 0
    for char in password:
        if char.isnumeric():
            digit += 1
        elif char.isupper():
            upper = True
        elif char.islower():
            lower = True

    if len(password) < 16:
        ans = 'BAD 1'
    if not(upper) or not(lower):
        if len(ans) == 7:
            ans = 'BAD 2'
        else:
            ans += ' 2'
    if digit < 6:
        if ans.count(' ') == 0:
            ans = 'BAD 3'
        else:
            ans += ' 3'

    return ans

num = int(input())
for i in range(num):
    password = input()
    print(good_password(password))
