using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        string badGene = Console.ReadLine();
        string goodGene = Console.ReadLine();
        string strand1 = Console.ReadLine();
        string strand2 = Console.ReadLine();

        string badGene2 = Translate(badGene);
        string goodGene2 = Translate(goodGene);

        string fixed1 = strand1.Replace(badGene, goodGene);
        fixed1 = fixed1.Replace(badGene2, goodGene2);

        string fixed2 = strand2.Replace(badGene, goodGene);
        fixed2 = fixed2.Replace(badGene2, goodGene2);

        Console.WriteLine(fixed1);
        Console.WriteLine(fixed2);
    }

    public static string Translate(string gene)
    {
        string gene2 = "";
        for (int i = 0; i < gene.Length; i++)
        {
            gene2 += geneMap[gene[i]];
        }
        return gene2;
    }

    public static Dictionary<char, char> geneMap = new Dictionary<char, char>()
    {
        {'A', 'T'},
        {'C', 'G'},
        {'G', 'C'},
        {'T', 'A'}
    };
}
