const P = parseInt(readline());
const N = parseInt(readline());
winner = [];
winnerP = 99999;
for (let i = 0; i < N; i++) {
    var inputs = readline().split(' ');
    const ROW = inputs[0];
    const Q = parseInt(inputs[1]);
    if(Q>P && Q<=winnerP)
    {
        if(Q==winnerP || winner.length == 0)
        {
            winner.push(ROW);
        }else{
            winner[0] = ROW
        }
        winnerP = Q
    }
}
winner = winner.sort();

console.log(winner.length>0?winner[0]:'IMPOSSIBLE');
