let inputs = readline().split(' ');
let x = parseInt(inputs[0]);
let y = parseInt(inputs[1]);
console.error(`Start x:${x} y:${y}`);
const n = parseInt(readline());
let vectors = [
    { x: 1, y: 0, o: "to the right" },
    { x: 0, y: 1, o: "to the top" },
    { x:-1, y: 0, o: "to the left" },
    { x:0,  y:-1, o: "to the bottom" }
];
let direction = 0;
for (let i = 0; i < n; i++) {
    const cmd = readline();
    switch (cmd)
    {
        case "LEFT":
            direction = (4 + direction + 1) % 4;
            console.error(`Turn left (direction: ${direction})`);
        break;

        case "RIGHT":
            direction = (4 + direction - 1) % 4;
            console.error(`Turn right (direction: ${direction})`);
        break;

        default:
            let distance = parseInt(cmd);
            x += vectors[direction].x * distance;
            y += vectors[direction].y * distance;
            console.error(`Move ${distance} ${vectors[direction].o} => x:${x} y:${y}`);
        break;
    }
}

console.log(`${x} ${y}`);
