class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        int s=0;
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < N; j++) {
                int value = in.nextInt();
                if(i<j) s+=value;
            }
        }

        // Write an answer using System.out.println()
        // To debug: System.err.println("Debug messages...");

        System.out.println(s);
    }
}
