public class Program
{
	public static int HI, WI;

	public static void Main()
	{
		string phrase = Console.ReadLine();
        WI = 1 + phrase.Length/2;
        HI = WI;

        for (int lin = 0 ; lin < HI ; lin++)
        {
            string s = phrase.Substring(lin,WI);
            Console.WriteLine(s);
        }		
	}
}
