using System; 

public class Program
{
	public static char[,] Grid;
	public static int Size;
	public static int MidPoint;
	public static int a;
	public static int b;
	public static char backChar ;
	public static void Main()
	{
        string[] inputs = Console.ReadLine().Split(' ');
        string symbol = inputs[0];
        a = int.Parse(inputs[1]);
        b = int.Parse(inputs[2]);
        backChar = symbol[0];
        Size = 31;
        MidPoint = Size/2;

        Grid = new char[Size,Size];
        InitGrid();
        Carve();

        PrintGrid();		
	}

	public static void Carve()
	{
		for (int lin = 0 ; lin < Size ; lin++)
		{
			for (int pos = 0 ; pos < Size ; pos++)
			{
				int adjPos = pos - MidPoint;
				int adjLin = lin - MidPoint;
				double temp1 = ((adjPos*1.0) / (a*1.0));
				double temp2 = ((adjLin*1.0) / (b*1.0));
				double ans = temp1 * temp1 + temp2 * temp2; 
				if (ans < 1) Grid[lin,pos] = ' ';
			}
		}
	}

	public static void PrintGrid()
	{
		for (int lin = 0 ; lin < Size ; lin++)
		{
			for (int pos = 0 ; pos < Size ; pos++)
			{
				char ch = Grid[lin,pos];
				Console.Write(ch);
			}
			Console.WriteLine();
		}
	}

	public static void InitGrid()
	{
		for (int lin = 0 ; lin < Size ; lin++)
		{
			for (int pos = 0 ; pos < Size ; pos++)
			{
				Grid[lin,pos] = backChar;
			}
		}
	}
}
