using System;
using System.Linq; 
using System.IO;
using System.Text;
using System.Collections; 
using System.Collections.Generic;

//https://techvify-software.com/35-best-coding-programming-quotes/

public class Program
{
	public static void Main()
	{
		{
            int numOfBlanks = int.Parse(Console.ReadLine());
            string quote = Console.ReadLine();

            var qRay = quote.Split();
            var qc = qRay.Count();
            var qbi = qc - 1;// biggest index of quote array

            int numOfBlanksPlus = numOfBlanks + 1; // to account for extra space at end.

            int qp = 0;//quote pointer (quote index pointer)

            int wordCount = 0;

            string proposedCummulativeWords = "";

            do
            {
                proposedCummulativeWords += qRay[qp] + " "; 
                if (proposedCummulativeWords.Length <= numOfBlanksPlus)
                {
                    wordCount += 1;
                    qp++;
                }
                else
                {
                    break;
                }
                if (qp > qbi) break; // quote Index pointer > quote biggest index
            } while (true);

            string ans = "";
            for (int index = 0 ; index < qp ; index++)
            {
                ans += qRay[index] + " ";
            }
            ans = ans.Trim();
            Console.WriteLine(ans);
            Console.WriteLine(wordCount + " words");
            Console.WriteLine(ans.Length + " characters long");
		}
	}
}
