using System; 

public class Program
{
	public static char[,] Grid;
	public static int height;
	public static int width;
	public static int modNumber;
	public static int desiredRemainder;
	public static char char1;
	public static char char2;
	public static int HI;
	public static int WI;
	public static void Main()
	{
        string twoCharacters = Console.ReadLine();
        string[] inputs = Console.ReadLine().Split(' ');
        height = int.Parse(inputs[0]);
        width = int.Parse(inputs[1]);
        modNumber = int.Parse(inputs[2]);
        desiredRemainder = int.Parse(inputs[3]);

        char1 = twoCharacters[0];
        char2 = twoCharacters[1];

        HI = height;
        WI = width;

        Grid = new char[HI,WI];
        InitGrid();
        Carve();
        PrintGrid();		
	}

	public static void PrintGrid()
	{
		for (int lin = 0 ; lin < HI ; lin++)
		{
			for (int pos = 0 ; pos < WI ; pos++)
			{
				char ch = Grid[lin,pos];
				Console.Write(ch);
			}
			Console.WriteLine();
		}
	}

	public static void Carve()
	{
		for (int lin = 0 ; lin < HI ; lin++)
		{
			for (int pos = 0 ; pos < WI ; pos++)
			{
				int temp1 = lin + pos;
                int temp2 = temp1 % modNumber;

				bool boo = temp2 == desiredRemainder; 
				if (boo) Grid[lin,pos] = char2;
			}
		}
	}

	public static void InitGrid()
	{
		for (int lin = 0 ; lin < HI ; lin++)
		{
			for (int pos = 0 ; pos < WI ; pos++)
			{
				Grid[lin,pos] = char1;
			}
		}
	}
}
