#include <stdio.h>

int main(void) {

  int n;
  scanf("%d", &n);

  if(n <= 0) {

    printf("No tables were sold");
    return 0;
  }

  int r = 0;

  for(int i = 1; i <= n; ++i) {

    if(i <= 5) r += 500;
    else if (i <= 10) r += 475;
    else if (i <= 20) r += 450;
    else r += 425;
  }

  printf("%d", (int)r/n);

  return 0;
}
