#include <stdio.h>
#include <string.h>

void switch_case(char, int*);

int main()
{
    char h[257];
    int s = 0;
    scanf("%[^\n]", h);
    for (int i = 0; i < strlen(h); i++)
    {
        switch_case(h[i], &s);
    }
    printf("%d\n", s);
    return 0;
}
void switch_case(char x, int* s)
{
    //I use switch here so that people understand the asignment
    switch (x)
    {
        case '1':
          *s += 1;
          break;
        case '2':
          *s += 2;
          break;
        case '3':
          *s += 3;
          break;
        case '4':
          *s += 4;
          break;
        case '5':
          *s += 5;
          break;
        case '6':
          *s += 6;
          break;
        case '7':
          *s += 7;
          break;
        case '8':
          *s += 8;
          break;
        case '9':
          *s += 9;
          break;
        case 'A':
          *s += 10;
          break;
        case 'B':
          *s += 11;
          break;
        case 'C':
          *s += 12;
          break;
        case 'D':
          *s += 13;
          break;
        case 'E':
          *s += 14;
          break;
        case 'F':
          *s += 15;
          break;
        }
}
