a, b = [int(i) for i in input().split()]
object_lines = []
for i in range(a):
    object_lines.append(list(input()))
c, d = [int(i) for i in input().split()]
grid_lines = []
for i in range(c):
    grid_lines.append(list(input()))

count = 0
for i in range(c-a+1):
    for j in range(d-b+1):
        ok = True
        for di in range(a):
            for dj in range(b):
                if grid_lines[i+di][j+dj] != '.' and object_lines[di][dj] != '.':
                    ok = False
        if ok:
            count += 1
            sol_i, sol_j = i, j

r = [str(count)]
if count == 1:
    for di in range(a):
        for dj in range(b):
            if object_lines[di][dj] != '.':
                grid_lines[sol_i+di][sol_j+dj] = object_lines[di][dj]
    for i in range(c):
        r.append("".join(grid_lines[i]))

print(*r, sep="\n")
