const gymnasts = readline().split(',');
const categories = readline().split(',');

const N = parseInt(readline());
records = [];
for (let i = 0; i < N; i++) {
    const row = readline().split(',');
    records.push({
    name:row[0], 
    bars:parseFloat(row[1]),
    beam:parseFloat(row[2]),
    floor:parseFloat(row[3])
  });
}

const getMaxInt = (array, selector) => Math.max(...array.map(selector));

maxValues = records
  .map(x => x.name)
  .reduce((values, name) => {
    if (!Object.keys(values).includes(name)) {
      const objectsWithMatchingId = records.filter(x => x.name === name);

      return {
	    ...values, 
	    [name]: {
          bars: getMaxInt(objectsWithMatchingId, x => x.bars),
          beam: getMaxInt(objectsWithMatchingId, x => x.beam),
          floor: getMaxInt(objectsWithMatchingId, x => x.floor)
        }
      }
    }

    return values;
  }, {});

gymnasts.forEach(gymnast => {
    console.log(categories.map(category => maxValues[gymnast][category]).join(','));
});

