def bucketise(string):
#	string = string.rstrip()
	buckets = {}
	for character in string:
		index = ord(character)
		if index in buckets.keys():
			buckets[index] += character
		else:
			buckets[index] = character

	for index in sorted(buckets.keys()):
		print(buckets[index])

bucketise(input())
