input()
rooms = [*input()]
rl = len(rooms)
n, k = map(int, input().split())

def mindist(ch1, ch2):
    o1 = rooms.index(ch1)
    o2 = rooms.index(ch2)
    return min((o1-o2+rl)%rl, (o2-o1+rl)%rl)

for _ in range(n):
    path = input().strip("#")
    if len(path) < 2:
        print("NOT SUS")
        continue

    hold = path[0]
    dist=0
    for i in range(1, len(path)):
        dist += 1
        if path[i]=="#":
            continue
        if mindist(path[i], hold)>dist:
            print("SUS")
            break

        hold = path[i]
        dist = 0

    else: print("NOT SUS")
