function unCamelCaser(input) {
    return input
        .replace(/([a-z])([A-Z])/g, '$1 $2') // Split camelCase by inserting space between lowercase and uppercase letters
        .split(/[\W_]+/) // Split the string by non-alphanumeric characters
        .map((word, index) =>
            index === 0
                ? word.charAt(0).toUpperCase() + word.slice(1).toLowerCase() // Capitalize the first word, and convert others to lowercase
                : word.toLowerCase() // Convert subsequent words to lowercase
        )
        .join(' '); // Join the array of words with spaces to form the final sentence
}

