#include <iostream>
#include <bits/stdc++.h>

using namespace std;

int main(void){
    string origin,word;
    cin>>origin>>word;

    string output = "";
    if(origin == "Latin"){
      for(int i = 0; i < word.length()-1; i++){
        if(word[i] == 's' && word[i+1] != 'c')output += "sc";
        if(word[i] == 'k')output += "c";
        if(word[i] != 'k' && (word[i] != 's' || (word[i] == 's' && word[i+1]=='c'))){
          output += word[i];
                }

      }
      if(word[word.length()-1]=='k')output += 'c';// handle the last letter since for loop is not reaching the last indexLaL
      else{
        output += word[word.length()-1];
      }
    }
    else{
      for(int i = 0; i < word.length(); i++){
        if(word[i] == 'i')output += "y";
        if(word[i] == 'f')output += "ph";
        if(word[i] == 'k')output += "ch";
        if(word[i] != 'i' && word[i] != 'f' && word[i] != 'k')output += word[i];
      }

    }

    cout<<output<<endl;
    return 0;
}
