import sys

donor_dict = { }
age_dict = { }
n = int( input( ) )

if n <= 0:
    print( "NO DONOR AVAILABLE" )
    sys . exit( )


for donor_num in range( n ) :
    name, blood_type, age = input( ) . split( )
    age = int( age )
    donor_dict [ name ] = [ blood_type, age ]

the_blood_type, age = input( ) . split( )
the_age = int( age )

for donor_num in donor_dict :
    name, blood_type, age = donor_num, donor_dict [ donor_num ] [ 0 ], donor_dict [ donor_num ] [ 1 ]

    if blood_type == the_blood_type :
        age_dict [ abs( age - the_age ) ] = name

ages = sorted( age_dict.keys() )

if len( ages ) == 0 :
    print( "NO COMPATIBLE DONOR" )
    sys . exit( )

print( "MOST COMPATIBLE DONOR : " + age_dict [ ages [ 0 ] ])

if len(ages) > 1:
    print( "OTHER COMPATIBLE DONORS :" )

for age in ages [ 1: ] :
    print( age_dict [ age ] )
