using System; using System.Linq; using System.IO; using System.Text; using System.Collections; using System.Collections.Generic;using System.Text.RegularExpressions;

public class Program
{				
	public static void Main()
	{
        int num = int.Parse(Console.ReadLine());
        for (int i = 0 ; i < num ; i++)
        {
            string idPlusMoneyAmt = Console.ReadLine();
            var inputArray = idPlusMoneyAmt.Split(' ');
            string ID = inputArray[0];
            string strDollar = inputArray[1];
            double dubDollar = Double.Parse(strDollar.Substring(1));
            int amt = (int)(dubDollar * 100);

            int remainder = amt % 25;

            var maxRay = new int[]{amt/10, amt/5}; 

            int counter = 0;
            for (int d = 0 ; d <= maxRay[0] ; d++)
            {
                for (int n = 0 ; n <= maxRay[1] ; n++)
                {	
                    int amtMinusQ =  d * 10 + n * 5 ;
                    if (amtMinusQ > amt) break;

                    if (amtMinusQ%25 == remainder)
                    {
                        int q = (amt - amtMinusQ)/25;
                        counter++;
                    }
                }
            }
            Console.WriteLine(ID + " " + counter);
        }		
    }
}
