e = int(input())
d = []
for x in range(int(input())):
    r = input().split()
    d.append((r[0],int(r[1])))
for dir in reversed(d):
    if dir[0] == 'M':
        e = e/dir[1]
    elif dir[0] == 'D':
        e *= dir[1]
    elif dir[0] == 'S':
        e += dir[1]
    elif dir[0] == 'A':
        e -= dir[1]
    elif dir[0] == 'R':
        e = e**dir[1]
    elif dir[0] == 'P':
        e = e**(1/dir[1])

print(round(e))
