import sys
equationIN = input().split()
Cdict = {}

if equationIN[0] == "-":
    t = 1

else:
    t = 0

for index in range(t,len(equationIN),2):
    sign = equationIN[index - 1]
    var = equationIN[index]

    if sign == "-":
        change = -1
    else:
        change = 1

    if var in Cdict:
        Cdict[var] += change

    else:
        Cdict[var] = change

equationOUT = ""

for C in Cdict:

    count = Cdict[C]

    if count > 1:
        equationOUT += "+" + str(count) + C

    elif count < -1:
        equationOUT += str(count) + C

    elif count == 1:
        equationOUT += "+" + C

    elif count == -1:
        equationOUT += "-" + C

if len(equationOUT) == 0:
    print(0)
    sys.exit()


if equationOUT[0] == "+":
    index = 1

else:
    index = 0

print(equationOUT[index:])
