messageIN = input().split()
n = int(input())
lenDICT = {}
messageOUT = []

for i in range(n):
    word = input()
    length = len(word)

    if length in lenDICT:
        lenDICT[length] += [word]

    else:
        lenDICT[length] = [word]

for word in messageIN:
    length = len(word)

    if word != "_" * length:
        messageOUT += [word]

    else:
        messageOUT += [lenDICT[length][0]]
        lenDICT[length] = lenDICT[length][1:]

print(*messageOUT)
