import java.util.Arrays;
import java.util.*;
import java.io.*;
import java.math.*;

public class Solution {
	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		String startPoint = in.nextLine();
        String stopName = in.nextLine();

		int nbMotBefore = 0;
		int nbMotAfter = 0;
		int nbLetterBefore = 0;
		int nbLetterAfter = 0;
		boolean found=false;
        stopName = stopName.replaceAll("\\:", " ").replaceAll("\\.", " ").replaceAll("\\!", " ").replaceAll("\\?", " ").replaceAll("\\,", " ");
        String[] st = stopName.split(" ");

        for(String s: st) {
        	if(!"".equals(s)) {
        		if(found) {
            		nbMotAfter++;
            		nbLetterAfter+=s.length();
        		} else if( isAnagram(s, startPoint)) {
            		found = true;
        		} else {
            		nbMotBefore++;
            		nbLetterBefore+=s.length();
            	}  
        	}
        }
        if(!found) {System.out.println("IMPOSSIBLE");}
        else {
            String s1 = String.valueOf(nbMotBefore);
            String s2 = String.valueOf(nbMotAfter);
            String s3 = String.valueOf(nbLetterBefore);
            String s4 = String.valueOf(nbLetterAfter);
            ///System.out.println(nbMotBefore+"."+nbMotAfter+"."+nbLetterBefore+"."+nbLetterAfter);
            System.out.println(s1.charAt(s1.length()-1)+"."+s2.charAt(s2.length()-1)+"."+s3.charAt(s3.length()-1)+"."+s4.charAt(s4.length()-1));
        }
	}

	private static boolean isAnagram(String s, String anagram) {
		 char a[] = anagram.toLowerCase().toCharArray();		 
	     Arrays.sort(a);
	     char b[] = s.toLowerCase().toCharArray();		 
	     Arrays.sort(b);
	     if(a.length != b.length) return false;
	     if(anagram.equals(s)) return false;
	     for(int i=0;i<a.length-1;i++) {
	    	 if(a[i] != b[i]) return false;
	     }
	     return true;
	}
}
