def is_prime(num):
    return num > 1 and not any(num % n == 0 for n in range(2,num))

r, c = [int(i) for i in input().split()]
M=[[0]*c for i in range(r)]
T=[[0]*r for i in range(c)]
for i in range(r):
    M[i]=input().split()

for i in range(r):
    for j in range(c):
        T[j][i]=M[i][j]
a=[]
for R in M:
    for i in range(c):
        for j in range(i+1,c+1):
            I=(''.join(p for p in R[i:j]))
            if int(I) not in a:
                a+=[int(I)]

for R in T:
    for i in range(r):
        for j in range(i+1,r+1):
            I=(''.join(p for p in R[i:j]))
            if int(I) not in a:
                a+=[int(I)]

print(len([i for i in set(a) if is_prime(i)]))
