s=list(input())
i=0
while s[i]!=".":
    while not s[i].isalpha():
        i+=1
    d=s[i]
    if d in "aeiouy":
        s[i]="eek"
    elif d in "AEIOUY":
        s[i]="Eek"
    elif d.islower():
        s[i]="ook"
    else:
        s[i]="Ook"
    i+=1
    while s[i].isalpha():
        del(s[i])
print("".join(s))
