const N = parseInt(readline());
const party = []
for (let i = 0; i < N; i++) {
    var inputs = readline().split(' ');
    const role = inputs[0];
    const damage = parseInt(inputs[1]);
    party.push({role,damage})
}
const strongest = party.sort((a,b)=>b.damage-a.damage).slice(0,4)
console.log(strongest[0].role)
console.log(strongest[2].role)
console.log(strongest[3].role)
console.log(strongest[1].role)
const coefficients = [1/2,2,1,1]
console.log(strongest.reduce( (a,m,i)=> a + m.damage * coefficients[i], 0))
