using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

static class Program{
    static void Main(string[] args)
    {
        var dimensions = Console.
        ReadLine().Split(' ').Select(x => int.Parse(x)).ToList();
        var image = new Image(new Coord(dimensions[0], dimensions[1]));
        for (int row = 0; row < image.size.row; row++){
            var newRow = new List<Pixel>();
            for (int col = 0; col < image.size.col; col++){
                var line = Console.ReadLine().Split(' ').Select(x => int.Parse(x)).ToList();
                newRow.Add(new Pixel(new Coord(row, col), line[0], line[1], line[2]));
            }
            image.pixels.Add(newRow);
        }

        var output = Blur(image);
        for (var r = 0; r < output.size.row; r++){
            for (var c = 0; c < output.size.col; c++){
                Console.WriteLine("{0} {1} {2}", output.pixels[r][c].red, output.pixels[r][c].green, output.pixels[r][c].blue);
            }
        }
    }

    static Image Blur(Image original){
        var result = new Image(original.size);
        for (var row = 0; row < original.size.row; row++){
            var newRow = new List<Pixel>();
            for (var col = 0; col < original.size.col; col++){
                var neighbors = new List<Coord>();
                // treating the pixel itself as its own neighbor
                neighbors.Add(new Coord(row, col));
                neighbors.Add(new Coord(row+1, col));
                neighbors.Add(new Coord(row-1, col));
                neighbors.Add(new Coord(row, col+1));
                neighbors.Add(new Coord(row, col-1));
                // Remove neighbor coordinates that aren't within the image bounds
                neighbors = neighbors.Where(x => x.row >= 0 && x.row < original.size.row && x.col >= 0 && x.col < original.size.col).ToList();
                var blurPixels = neighbors.Select(x => original.pixels[x.row][x.col]);
                var red = (int)Math.Floor((double)blurPixels.Select(x => x.red).Sum() / blurPixels.Count());
                var green = (int)Math.Floor((double)blurPixels.Select(x => x.green).Sum() / blurPixels.Count());
                var blue = (int)Math.Floor((double)blurPixels.Select(x => x.blue).Sum() / blurPixels.Count());
                newRow.Add(new Pixel(new Coord(row, col), red, green, blue));
            }
            result.pixels.Add(newRow);
        }
        return result;
    }
}

public class Coord{
    public int row, col;

    public Coord(int _row, int _col){
        row = _row;
        col = _col;
    }
}

public class Image{
    public List<List<Pixel>> pixels;
    public Coord size;

    public Image(Coord _size){
        pixels = new List<List<Pixel>>();
        size = _size;
    }
}

public class Pixel{
    public Coord location;
    public int red, green, blue;
    public Pixel(Coord _location, int _red, int _green, int _blue){
        location = _location;
        red = _red;
        green = _green;
        blue = _blue;
    }
}
