const N = parseInt(readline());
for (let i = 0; i < N; i++) {
    const names = readline().toLowerCase();
    if(names.includes("w") && names.includes("i") && names.includes("z") && names.includes("a") && names.includes("r") && names.includes("d")){
        console.log("Is a wizard")
    }else{
        console.log("Not a wizard")
    }
}
