using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        int n = int.Parse(Console.ReadLine());
        List<Pool> Pools = new List<Pool>();
        for (int i = 0; i < n; i++)
        {
            string choice = Console.ReadLine();
            Pools.Add(new Pool(choice));
        }
        Pools.Sort(Pool.SortByVolume);
        foreach(Pool tmp in Pools)
        {
            Console.WriteLine(tmp.PoolName + " " + Math.Round(tmp.Vol, 2).ToString());
        }
    }

    class Pool
    {
        public string PoolName;
        public string NamedType;
        public int PoolType = 0; // 0 = Unknown, 1 = Rect, 2 = Circular
        public float A = 0;
        public float B = 0;
        public float C = 0;
        public float Vol = 0;
        public Pool(string line)
        {
            string[] s = line.Split(' ');
            PoolName = s[0];
            NamedType = s[1];
            A = float.Parse(s[2]);
            B = float.Parse(s[3]);
            C = float.Parse(s[4]);
            switch(NamedType.Substring(0, 1).ToUpperInvariant())
            {
                case "R": // Rectangular
                    Vol = A * B * C; // w x l x h
                    PoolType = 1;
                    break;
                case "C":
                    Vol = (float)(Math.PI * A * A * B); // π x (Radius)² x d
                    PoolType = 2;
                    break;
                default:
                    PoolType = 0;
                    break;
            }
        }
        public static int SortByVolume(Pool one, Pool other)
        {
            if (one.Vol < other.Vol) return -1;
            if (one.Vol > other.Vol) return 1;
            return 0;
        }
    }
}
