data.solution:
reference_phrase = input()
incomplete_phrase = input()

data = {}
for c1, c2 in zip(reference_phrase, reference_phrase[1:]):
    data.setdefault(c1, {})
    data[c1].setdefault(c2, 0)
    data[c1][c2] += 1

r = incomplete_phrase[0]
for c1, c2 in zip(incomplete_phrase, incomplete_phrase[1:]):
    if c2 == '_' and c1 in data:
        r += sorted(list(data[c1].keys()), key=data[c1].__getitem__)[-1]
    else:
        r += c2

print(r)
