#include <ctype.h>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int	ft_count_words(const char *str, char c)
{
	int	i;
	int	j;

	i = 0;
	j = 0;
	while (str[i])
	{
		while ((str[i]) && (str[i] == c))
			i++;
		if (str[i])
		{
			while (str[i] && str[i] != c)
				i++;
			j += 1;
		}
	}
	return (j);
}

int	ft_len(const char *str, char c)
{
	int	i;

	i = 0;
	while (str[i] && str[i] != c)
	{
		i++;
	}
	return (i);
}

char	**ft_free_allocation(char **s, int k)
{
	while (k >= 0)
	{
		free(s[k]);
		k--;
	}
	free(s);
	return (NULL);
}

char	**spl(char const *s1, char c)
{
	char	**arr;
	int		k;
	int		j;

	k = 0;
	if (s1 == NULL)
		return (NULL);
	arr = (char **)calloc((ft_count_words(s1, c) + 1), sizeof(char *));
	if (!arr)
		return (NULL);
	while (*s1)
	{
		while (*s1 && *s1 == c)
			s1++;
		if (*s1)
		{
			arr[k] = (char *)calloc(ft_len(s1, c) + 1, sizeof(char));
			if (!arr[k++])
				return (ft_free_allocation(arr, k - 1));
			j = 0;
			while (*s1 && (*s1 != c))
				arr[k - 1][j++] = *(s1++);
		}
	}
	return (arr);
}

int main()
{
    int N;
    int i = 0;
    scanf("%d\n", &N);
    char **save = (char **)malloc(sizeof(char *) *(N + 1));
    while (i < N)
    {
        char S[1000];
        scanf("%[^\n]", S); fgetc(stdin);
        save[i] = strdup(S);
        if (!save[i])
            exit(1);
        i++;
    }
    save[i] = NULL;
    i = 0;
    char **split_in;
    int arr[N];
    while (save[i])
    {
        int Points, goals_points, Assists_points, NT_points, club_points;
        split_in = spl(save[i], '-');
        if (!split_in)
        {
            i = 0;
            while (save[i])
                free(save[i++]);
            exit(1);
        }
        goals_points = atoi(split_in[3]) * 2;
        Assists_points = atoi(split_in[4]);
        NT_points = atoi(split_in[5]) * 20;
        club_points = atoi(split_in[6]) * 10;
        Points = goals_points + Assists_points + NT_points + club_points;
        arr[i] = Points;
        i++;
    }
    i = 0;
    int WinnerPoints = 0, Winner_index;
    while (save[i])
    {
        if (WinnerPoints < arr[i])
        {
            WinnerPoints = arr[i];
            Winner_index = i;
        }
        i++;
    }
    int j = 0;
    while (save[Winner_index][j] && !isdigit(save[Winner_index][j]))
    {
        if (save[Winner_index][j] == '-')
            printf(" ");
        else
            printf("%c", save[Winner_index][j]);
        j++;
    }
    printf("%d\n", WinnerPoints);
    i = 0;
    while (save[i])
        free(save[i++]);
    free(save);
    i = 0;
    while (split_in[i])
        free(split_in[i++]);
    free(split_in);
}
