        bits = N*8;
        break;
    case 'KB':
        bits = N*8*1024;
        break;
    case 'MB':
        bits = N*8*1024*1024;
        break;
    case 'GB':
        bits = N*8*1024*1024*1024;
        break;
    case 'TB':
        bits = N*8*1024*1024*1024*1024;
        break;
    default:
}

while( 2**qbits < bits) {
    qbits++
}

console.error(N);
console.error(S);
console.error(bits+' bits');
console.log(N==0?N:qbits);
