#include <iostream>
#include <string>
#include <vector>
using namespace std;

int main()
{
	int v ;
	cin >> v;
	vector<string>t1 = { "A" };
	vector<string>t2;
	for (int I = 0; I < v; I++) {
		for (auto const element : t1)
		{
			cout << element;
			t2.push_back("A");
			if (element.compare("A")==0) {
				t2.push_back("B");
			}
		}
		t1 = t2;
		t2.clear();
		cout << "\n";
	}
	return 0;
}
