def to_grams(quantity):
    "Convert a g/kg str to an integer quantity in gramms"
    if quantity[-2:]=='kg': return int(float(quantity[:-2])*1000)
    else: return int(quantity[:-1])

def to_cl(quantity):
    "Convert a cl/L str to an integer quantity in centiliters"
    if quantity[-1:]=='L': return int(float(quantity[:-1])*100)
    else: return int(quantity[:-2])

def liquid_output(quantity):
    if quantity<100: return str(quantity)+'cl'
    else: return str(float(quantity/100))+'L'

def solid_output(quantity):
    if quantity<1000: return str(quantity)+'g'
    else: return str(float(quantity/1000))+'kg'


num_recipe, num_ingredients = map(int,input().split())
recipe = {}
unit = {}
for i in range(num_recipe):
    line = input().split()
    if line[0]=='-':
        ing = ' '.join(line[2:])
        unit[ing] = 0 if 'g' in line[1] else 1
        recipe[ing] = to_grams(line[1]) if 'g' in line[1] else to_cl(line[1])

ingredients = {}
for i in range(num_ingredients):
    line = input().split()
    ing = ' '.join(line[:-1])
    quantity = [to_grams,to_cl][unit[ing]](line[-1])
    ingredients[ing] = quantity


ing_list = ingredients.keys()
num_cooking = 0
while all([i>0 for i in ingredients.values()]):
    for ing in ing_list:
        ingredients[ing] -= recipe[ing]
    num_cooking += 1
for ing in ing_list:
    if ingredients[ing]==0:
        print(ing)
        break
print(num_cooking)
remaining = [(ing,ingredients[ing]) for ing in ing_list if ingredients[ing]>0]
for ing in sorted(remaining,key=lambda a:a[1]+999999*unit[a[0]]):
    print(ing[0],[solid_output,liquid_output][unit[ing[0]]](ing[1]))
