import java.util.*;

class Solution {
    static Map<String, Integer> proprietes;

    public static void main(String [] args) {
        Scanner in = new Scanner(System.in);
        int P = Integer.parseInt(in.nextLine());
        proprietes = new HashMap<>();
        for (int i = 0; i < P; i++) {
            proprietes.put(in.nextLine(), i);
        }
        int N = Integer.parseInt(in.nextLine());

        List<Person> persons = new ArrayList<>();
        for (int i = 0; i < N; i++) {
            persons.add(new Person(in.nextLine().split(" ")));
        }

        int F = Integer.parseInt(in.nextLine());
        for (int i = 0; i < F; i++) {
            Formula formula = new Formula(in.nextLine());
            System.out.println(persons.stream().filter(formula::match).count());
        }

    }

    static class Formula {

        final List<Filter> filters;

        Formula(String line) {
            filters = Arrays.stream(line.split(" AND ")).map(Filter::new).toList();
        }

        boolean match(Person person) {
            return filters.stream().allMatch(cr -> cr.match(person));
        }
    }

    static class Filter {
        final int propIndex;
        final String expected;

        Filter(String strCritere) {
            String[] tokens = strCritere.split("=");
            propIndex = proprietes.getOrDefault(tokens[0], -1);
            expected = tokens[1];
        }

        boolean match(Person person) {
            return propIndex != -1 && expected.equals(person.values.get(propIndex));
        }
    }

    static class Person {
        String name;
        List<String> values;

        Person(String[] line) {
            name = line[0];
            values = Arrays.stream(line, 1, line.length).toList();
        }
    }
}
