import java.util.*;

class Solution {
    public static void main(String args[]) {
        HashMap<String, ArrayList<Integer>> players = new HashMap<String, ArrayList<Integer>>();
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        for (int i = 0; i < N; i++) {
            String command = in.nextLine();
            String player = command.split(" ")[0];
            String mode = command.split(" ")[1];
            int amount = Integer.valueOf(command.split(" ")[2]);
            ArrayList<Integer> data = new ArrayList<Integer>();
            if (players.get(player) != null) {
                if (mode.equals("A")) {
                    data.add(players.get(player).get(0) + amount * players.get(player).get(1));
                    data.add(players.get(player).get(1));
                } else {
                    data.add(players.get(player).get(0));
                    data.add(players.get(player).get(1) + amount);
                }
            } else {
                if (mode.equals("A")) {
                    data.add(amount * 5);
                    data.add(5);
                } else {
                    data.add(0);
                    data.add(5 + amount);
                }
            }
            players.put(player, data);
        }

        int bestScore = -1001;
        ArrayList<String> bestPlayers = new ArrayList<String>();
        for (Map.Entry<String, ArrayList<Integer>> player : players.entrySet()) {
            if (player.getValue().get(0) > bestScore) {
                bestPlayers.clear();
                bestPlayers.add(player.getKey());
                bestScore = player.getValue().get(0);
            } else if (player.getValue().get(0) == bestScore) {
                bestPlayers.add(player.getKey());
            }
        }
        System.err.println(players);
        String answer = "";
        for (String player : bestPlayers) {
            answer += player + " ";
        }
        System.out.println(answer.trim());
        System.out.println(bestScore);
        in.close();
    }
}
