heads, feet = map(int, input().split())
types = []
name1, c1 = input().split()
c1 = int(c1)
name2, c2 = input().split()
c2 = int(c2)

possible = True
x2 = (feet - c1 * heads) // (c2 - c1)
if (feet - c1 * heads) % (c2 - c1) != 0:
    possible = False

x1 = heads - x2

if x1 < 0 or x2 < 0:
    possible = False

def output(name, x):
    if x != 1:
        print(f'{x} {name}s')
    else:
        print(f'{x} {name}')

if possible:
    output(name1, x1)
    output(name2, x2)
else:
    print('impossible')
