using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        int P(string t) => int.Parse(t);
        int n = P(Console.ReadLine());
        string[] inputs = Console.ReadLine().Split(' ');
        int f=P(inputs[0]), c=P(inputs[1]), x=P(inputs[2]);
        for(int i = 0; i < n; i++)
        {
            string crytedQuote = Console.ReadLine();
            int ct = c;
            string o = "";
            for(int j=f-1;j<crytedQuote.Length;j+=x)
            {
                if ((j + ct) >= crytedQuote.Length) ct = crytedQuote.Length - j;
                o += crytedQuote.Substring(j,ct);
            }
            Console.WriteLine(o);
        }
    }
}
