import java.awt.Point;
import java.util.Arrays;
import java.util.HashSet;
import java.util.Scanner;
import java.util.Set;

public class Solution {

	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int n = in.nextInt();
		for (int i = 0; i < n; i++) {
			int p = in.nextInt();
			int r = in.nextInt();

			Islet.r = r;
			boolean fail = false;

			// all points should be unique
			Set<Point> set = new HashSet<>();

			Islet.iArr = new Islet[p];
			for (int j = 0; j < p; j++) {
				int x = in.nextInt();
				int y = in.nextInt();
				if (y > r) {
					fail = true;
				}
				Islet.iArr[j] = new Islet(x, y);
				boolean result = set.add(new Point(x, y));

				if (!result) {
					System.err.println("point " + x + "," + y + " is not unique");
				}
			}

			// no solution in this case
			if (fail) {
				System.out.println(-1);
				continue;
			}

			Islet.sort();

			for (Islet ii : Islet.iArr) {
				ii.setCenter();
			}

			process(r);
		} // for n
		in.close();
	}

	private static void process(int r) {
		int count = 0; // transmitter count
		Islet[] iArr = Islet.iArr;
		int len = iArr.length;

		for (int i = 0; i < len; i++) {
			if (!iArr[i].covered) {
				double cx = iArr[i].cx;

				boolean found = true;
				for (int j = 0; j < len; j++) {
					if (iArr[j].x <= cx) {
						if (!iArr[j].covered && i != j) {
							if (!isCoveredByCircle(iArr[j], cx, r)) {
								found = false;
								break; // away from for j
							}
						}
					}
					// no need to take care of the right
					else {
						break;
					}
				} // for j

				if (found) {
					count++;

					for (int j = 0; j < len; j++) {
						// sure it is covered
						if (i == j) {
							iArr[j].covered = true;
						}
						// L
						if (iArr[j].x <= cx) {
							iArr[j].covered = true;
						}
						// R
						else if (iArr[j].x > cx) {
							if (!iArr[j].covered) {
								if (isCoveredByCircle(iArr[j], cx, r)) {
									iArr[j].covered = true;
								}
							}
						}
					}
				} // if found
			} // if not covered
		} // for i

		System.out.println(count);
	}

	private static boolean isCoveredByCircle(Islet i2, double rx, int d) {
		return (i2.y * i2.y) + ((rx - i2.x) * (rx - i2.x)) <= d * d;
	}
}

class Islet implements Comparable<Islet> {
	static int r;
	static Islet[] iArr;

	int x, y;
	boolean covered = false;
	double cx; // center

	public Islet(int x, int y) {
		this.x = x;
		this.y = y;
	}

	public static void sort() {
		Arrays.sort(iArr);
	}

	public void setCenter() {
		double dx = Math.sqrt(r * r - this.y * this.y);
		this.cx = dx + this.x;
	}

	@Override
	public int compareTo(Islet o) {
		if (this.x == o.x) {
			return Integer.compare(this.y, o.y);
		}
		return Integer.compare(this.x, o.x);
	}

	@Override
	public int hashCode() {
		final int prime = 31;
		int result = 1;
		result = prime * result + x;
		result = prime * result + y;
		return result;
	}

	@Override
	public boolean equals(Object obj) {
		if (this == obj)
			return true;
		if (obj == null)
			return false;
		if (getClass() != obj.getClass())
			return false;
		Islet other = (Islet) obj;
		if (x != other.x)
			return false;
		if (y != other.y)
			return false;
		return true;
	}
}
