const str = readline();
const inputbase = parseInt(readline());
const outputbase = parseInt(readline());

const alpha = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';

// parse with inputbase
let value=0;
for(let c of str){
    value*=inputbase;
    value+=alpha.indexOf(c)
}

// format with outputbase
let result = ''
while(value){
    result=alpha[value%outputbase]+result
    value=Math.floor(value/outputbase);
}

console.log(result)
